import React from 'react'
import axios from 'axios';

export const Register = (data) => {
  console.log(5656,data)
  return (dispatch) => {
  const body = {
      "user":{
          "email":data.email,
          "password":data.password,
          "password_confirmation":data.cpassword
      }
  }
  axios.post(`https://react-rails-api-demo.herokuapp.com/api/v1/signup`,body)
      .then(res => {
          console.log(res.data)
        localStorage.setItem('user-info', JSON.stringify(res.data));
        dispatch({
            type : "GET_LOGUP",
            payload : res.data
        });
      })

  };
}

export const getLogin = (data) => {
    console.log(5656,data)
    return (dispatch) => {
    const body = {
        "user":{
            "email":data.email,
            "password":data.password
        }
    }
    axios.post(`https://react-rails-api-demo.herokuapp.com/api/v1/signin`,body)
        .then(res => {
            console.log(res.data)
          localStorage.setItem('user-info', JSON.stringify(res.data));
          localStorage.setItem('token',res.data.token);
          dispatch({
              type : "GET_LOGIN",
              payload : res.data
          });
        })

    };
  };


  export const GetData = () => {
    const token = localStorage.getItem('user-token');
    console.log("user-token", token)

    return axios.request({
        method: "get",
        headers: {
            "Content-Type": "application/json",
            "Authorization": token
        },
        url: "https://react-rails-api-demo.herokuapp.com/api/v1/posts"
    })
}
