        dispatch(GetData());
    },[]);

    function Singlepost(id)
    {
      Spost(id);
     window.location.href='/Spost'
    }

  return (
    <div>
        <Link/>
        <h1>Dashboard </h1>
        <Table striped bordered hover className='container'>
  <thead>
    <tr>
      <th>ID</th>
      <th>Title</th>
      <th>Description</th>
      <th>User_Id</th>
      <th>Operation</th>
    </tr>
  </thead>
  <tbody>


      {data?.map((i,index)=>{
            return (
          <tr key= {index}>
          <td>{i.id}</td>
          <td>{i.title}</td>
          <td>{i.description}</td>
          <td>{i.user_id}</td>
          <td><Button variant="primary" onClick={()=>Singlepost(i.id)} > view</Button> <Button variant="success">Edit</Button> <Button variant="danger">Delete</Button></td>
          </tr>)
        })}


  </tbody>
</Table>

    </div>
  )
}
