import React from 'react'
import { useForm } from "react-hook-form";
import { useDispatch } from 'react-redux';
import { getLogin } from '../actions/action';

export default function Login() {
  const dispatch = useDispatch ()
  const { register, handleSubmit, watch, formState: { errors } } = useForm();
  const onSubmit = data => {
    console.log(22,data);
    dispatch(getLogin(data))

  }

  // console.log(watch("example")); // watch input value by passing the name of it
  return (
    <div>
      <h1>Login Form</h1><br/>
    <form onSubmit={handleSubmit(onSubmit)}>
      {/* register your input into the hook by invoking the "register" function */}
      <input type="text" name='email'  {...register("email")} placeholder="Enter Email"/><br/><br/>

      {/* include validation with required or other standard HTML validation rules */}
      <input type="password" name='password' {...register("password", { required: true })} placeholder="Password"/><br/>
      {/* errors will return when field validation fails  */}
      {/* {errors.exampleRequired && <span>This field is required</span>}<br/><br/> */}

      <input type="submit" />
    </form>
    </div>
  )
}
